#!/bin/sh
ess category add tweets 'filetypes/Twee*' --preprocess 'zcat | jsncnv -f,eok,qui - -d s:created_at:created_at s:id:id_str s:text:text s:source:source s:user_id:user.id_str s:usr_location:user.location i:followers:user.followers_count i:friends:user.friends_count i:listed:user.listed_count i:favourites:user.favourites_count i:statuses:statuses_count s:usr_created_at:user.created_at s:usr_timezone:user.time_zone s:usr_lang:user.lang s:is_translator:user.is_translator f:geo_lat:geo.coordinates[0] f:geo_lon:geo.coordinates[1] s:place_id:place.id s:url:place.url s:place_type:place.place_type s:place_name:place.name s:place_full_name:place.full_name s:place_country_code:place.country_code s:place_country:place.country s:filter_level:filter_level s:lang:lang s:timestamp:timestamp_ms' --archive '*gz'
